import React, { useState } from "react";
import Form from 'react-bootstrap/Form'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import { validateEmail } from "../../utils/helpers";

function ContactForm() {
  const [formState, setFormState] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [errorMessage, setErrorMessage] = useState("");
  const { name, email, message } = formState;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!errorMessage) {
      setFormState({ [e.target.name]: e.target.value });
      console.log("Form", formState);
    }
  };

  const handleChange = (e) => {
    if (e.target.name === "email") {
      const isValid = validateEmail(e.target.value);
      if (!isValid) {
        setErrorMessage("Your email is invalid.");
      } else {
        setErrorMessage("");
      }
    } else {
      if (!e.target.value.length) {
        setErrorMessage(`${e.target.name} is required.`);
      } else {
        setErrorMessage("");
      }
    }
  };

  return (
    <section>
      <h2 className='contact-section' data-testid='h1tag'>
        {" "}
        Say <em>Hi!,</em> <em>I'd like to</em> hear from you!
      </h2>
      <div className='form'>
      <form className='contact-form' id='contact-form' onSubmit={handleSubmit}>
        <div className='contact-input'>
          <label htmlFor='name'>Name:</label>
          <input
            type='text'
            name='name'
            size='60'
            defaultValue={name}
            onBlur={handleChange}
          />
        </div>
        <div className='contact-input'>
          <label htmlFor='email'>Email address:</label>
          <input
            type='email'
            name='email'
            size='60'
            defaultValue={email}
            onBlur={handleChange}
          /> 


        </div>
        <div className='contact-input'>
          <label htmlFor='message'>Message:</label>
          <textarea
            name='message'
            rows='5'
            cols='60'
            defaultValue={message}
            onBlur={handleChange}
          />
        </div>
        {errorMessage && (
          <div>
            <p className='error-text'>{errorMessage}</p>
          </div>
        )}
          <div style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center"
        }}>
          <button className='btns' data-testid='button' type='submit'>
            Submit
          </button>
          </div>
      </form>
      </div>
      <div className='contact-simple'>
      <h4>
        Or just Keep it <em>Simple.</em>
      </h4>
      <p>
        Email: Brookemadisondesign@gmail.com
      </p>
      <p>
        Phone Number: 904-207-1528
      </p>
      </div>
    </section>
  );
}

export default ContactForm;
