import React, { useState } from "react";
import Modal from '../Modal';
import { Button } from 'react-bootstrap'

function Project({ category }) {
  const [featuredProjects] = useState([
    {
      index: 0,
      name: 'Project Three',
      shortdesc: 'Coming soon..',
      tech: 'HTML, CSS, JavaScript, Node.js, Express.js, MongoDB, Mongoose ODM, GraphQL, JWT, Heroku',
      description: 'description coming soon',
      github: 'https://github.com/brookemadison/',
    },
    {
      index: 1,
      name: 'Decide Eat',
      shortdesc: 'Decide what to eat, drink, and listen to on your date night',
      tech: 'HTML, CSS, JavaScript, jQuery, Bulma, Spoonacular API, Spotify API',
      description: 'Decide Eat was crafted to offer a unique experience on date night in. This app will elevate your date night by bringing the fun to the comfort of your home. Remove the stress of deciding by taking the quiz that pairs you with a recipe, cocktail, and music playlist recommendation.',
      github: 'https://github.com/Padredilg/decide-eat',
      deployed: 'https://padredilg.github.io/decide-eat/'
    },
    {
      index: 2,
      name: 'Pokedex',
      shortdesc: 'An online Pokédex that allows users to view and search for their favorite original 151 Pokémon.',
      tech: 'HTML, CSS, JavaScript, Node.js, Express.js, MySQL, dotenv, Heroku, JawsDB, Sequeliz, Bcrypt, express-session, Handlebars, animate.css, PokéAPI',
      description: 'The user MUST create an account first by username, email and password. The password MUST be at least 10 characters long. Once logged in, the user is then sent to the dashboard where they are presented with a search form. In this form they may type in the name of any of the original 151 Pokémon. Once the data is sent, the result brings up a card. The Pokémon stats will display along with the username written on the card. Congratulations! You caught your first Pokémon! To do another search simply click on the dashboard button. When you have finished your hunt, you may hit the logout button!',
      github: 'https://github.com/Lightfooted/pokedex-project',
      deployed: 'https://protected-hamlet-00069.herokuapp.com/'
    },
  ]);
  const [nonFeaturedProjects] = useState([
    {
      index: 3,
      name: 'Budget Tracker',
      shortdesc: 'A budget tracker that can be used without an internet connection',
      tech: 'HTML, CSS, JavaScript, indexedDb, Service-Workers, manifest.json, express.js, MongoDB, Mongoose',
      description: 'Having offline functionality is paramount to the success of an application that handles users’ financial information. This budget tracker application allows for offline access and functionality. The user will be able to add expenses and deposits to their budget with or without a connection. If the user enters transactions offline, the total should be updated when they are brought back online.',
      github: 'https://github.com/brookemadison/budget-tracker',
      deployed: 'https://lit-reaches-08410.herokuapp.com/'
    },
  ]);

  const [currentProject, setCurrentProject] = useState({});

  const [show, setShow] = useState(false);
  const handleShow = (currentProj) => {
    setCurrentProject(currentProj)
    setShow(true);
  };
  const handleClose = () => setShow(false);

  return (
    <div>
      {/* featured projects */}
      <div className="projects-wrapper">
        {featuredProjects.map((project, i) => (
          <Button
            className="project-button"
            variant="link"
            onClick={() => { handleShow(featuredProjects[i]) }}
          >
            <img
              src={require(`../../assets/projects/${i}.png`).default}
              alt={project.name}
              className="project-image featured-project"
              key={project.name}
            />
            <div class="project-label">
              <h6 className="label-title">{project.name}</h6>
              <p className="label-description">{project.shortdesc}</p>
              <p className="label-read-more">Click to Read More</p>
            </div>

          </Button>
        ))}
      </div>
      {/* non-featured-projects */}
      <div className="projects-wrapper">
        {nonFeaturedProjects.map((project, i) => (
          <Button
            className="project-button"
            variant="link"
            onClick={() => { handleShow(nonFeaturedProjects[i]) }}
          >
            <img
              src={require(`../../assets/projects/${project.index}.png`).default}
              alt={project.name}
              className="project-image"
              key={project.name}
            />
            <div class="project-label small-label">
              <h6 className="label-title">{project.name}</h6>
              <p className="label-description">{project.shortdesc}</p>
              <p className="label-read-more">Click to Read More</p>
            </div>

          </Button>
        ))}
      </div>
      {show ? <Modal currentProject={currentProject} onClose={handleClose} /> : null}
    </div>
  )
}

export default Project;
